/* Create sentence */
/* mun_ucreacion and mun_fcreacion refers to User and Date of creation respectively */

create table loc_municipios
(
	mun_id serial primary key,
	mun_nombre text not null,
	dep_id int not null references loc_departamentos(dep_id),
	mun_ucreacion text not null,
	mun_fcreacion timestamp not null,
	estado char(3)
);

